import { useState } from 'react';
import questions from './questions';
import Button from 'react-bootstrap/Button';
import './RandomNumberGame.css'


const RandomNumberGame = () => {
    const [numbers, setNumbers] = useState([...Array(100)].map((_, index) => index + 1));
    const [currentNumber, setCurrentNumber] = useState(null);
    const [currentQuestion, setCurrentQuestion] = useState('');

    const getNextNumber = () => {
        if (numbers.length === 0) {
            setCurrentNumber(null);
            setCurrentQuestion('');
            return;
        }

        const randomIndex = Math.floor(Math.random() * numbers.length);
        const number = numbers[randomIndex];
        const updatedNumbers = [...numbers];
        updatedNumbers.splice(randomIndex, 1);

        setNumbers(updatedNumbers);
        setCurrentNumber(number);

        // Generate a random question based on the number
        const question = questions[number];
        setCurrentQuestion(question);

    };
    return (
        <>
            <div>
                <Button  variant="dark" onClick={getNextNumber}>Generate Random Number</Button>
                {currentNumber && (
                    <div>
                        <p>The number is: {currentNumber}</p>
                        <p>{currentQuestion}</p>
                    </div>
                )}
                {!currentNumber && 
                <p>Game over! All numbers have been used.</p>
                }
            </div>
        </>
    )
}

export default RandomNumberGame
